#################################
#
# Defines some variables that say
# where Sire is installed etc.
#

import sys, os

try:
  FES_HOME = os.environ['FES_HOME']
except KeyError:
  print >>sys.stderr, 'environment variable FES_HOME not set'
  sys.exit(1) 


binary_directory = os.path.join(FES_HOME, 'bin')
library_directory = os.path.join(FES_HOME, 'lib')
include_directory = os.path.join(FES_HOME, 'include', 'Sire')
share_directory = os.path.join(FES_HOME, 'share', 'Sire')
parameter_directory = os.path.join(share_directory, 'parameters')
